package week1.day1;

public class PrintEvenNumbersinReverse {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		
		for (int j=20; j>0; j--){
			if (j%2 == 0){
				System.out.println("Even numbers :"+ j);
			}
			
	}

}
}
